from morphopy.neurontree import NeuronTree as nt from morphopy.computation import file_manager as fm
N = fm.load_swc_file("../data/EC3-80604.CNG.swc") Dendrites = N.get_dendritic_tree()
from morphopy.neurontree.plotting import show_threeview fig = plt.figure(figsize=(10,10)) show_threeview(N, fig)

from morphopy.computation.feature_presentation import get_persistence
import numpy as np def custom_distance(G, u, v): """ Returns a distance between nodes u and v, which both are part of the graph given in G. """ n = G.node[u]['pos'] r = G.node[v]['pos'] return np.dot(n, r)
df = get_persistence(Dendrites.get_topological_minor()) df_custom = get_persistence(Dendrites.get_topological_minor(), f=custom_distance)
